import React from 'react';
import { ProgressBar } from 'react-bootstrap';

function Dashboard() {
  // Define the project data as an array of objects
  const projects = [
    {
      title: 'EcoTasks',
      progress: 51,
      description:
        'Pending review: Environmental monitoring using drones with AI capabilities.',
    },
    {
      title: 'Berekesu Farms',
      progress: 75,
      description:
        'Pending review: Sustainable agriculture using AI and machine learning.',
    },
    {
      title: 'Ashesi Culture',
      progress: 0,
      description: 'No description available.',
    },
  ];

  // Return the JSX code for the dashboard component
  return (
    <div className="bg-pink-200 min-h-screen">
      <div className="container mx-auto p-4">
        <h1 className="text-white text-4xl font-bold">Welcome, Bright Sithole</h1>
        <img
          src="https://i.imgur.com/9qZ2XyL.jpg"
          alt="Profile picture"
          className="w-32 h-32 rounded-full mt-4"
        />
        <h2 className="text-white text-3xl font-semibold mt-8">Your Projects</h2>
        <div className="grid grid-cols-3 gap-4 mt-4">
          {projects.map((project) => (
            <div
              key={project.title}
              className="bg-white p-4 rounded-lg shadow-lg"
            >
              <h3 className="text-pink-600 text-xl font-bold">
                {project.title}
              </h3>
              <ProgressBar
                variant="pink"
                now={project.progress}
                label={`${project.progress}%`}
                className="mt-2"
              />
              <p className="text-gray-600 text-sm mt-2">{project.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
