import React from 'react';
import BackgroundPage from '../pages/backgroundpage.js';
import LoginPage from '../components/login.js';
import '../styles/loginpage.css'; // Import your dashboard.css file


function TheLogin() {
  const logo = '/images/ARRM_logo.png';
  const logo_2 = '/images/background_image.png';


   return (
    <div className="bg-container relative">
      {/* The login page component */}
      <div className="left-half bg-gradient-to-r from-red-500 to-white h-screen flex relative z-10">
        <LoginPage />
      </div>

      {/* Your existing background elements */}
      <div className="right-half bg-white z-20">
        <img src={logo} alt="ARRM Logo" className="top-right-image" />
      </div>

      {/* Background image behind the login page */}
      <div className="absolute inset-0 z-0">
        <img src={logo_2} alt="Big design" className="center-image w-full h-full object-cover" />
      </div>
    </div>
  );

  
}

export default TheLogin;
