import React from 'react';
import BackgroundPage from '../pages/backgroundpage.js';
import LoginPage from '../components/login.js';
import '../styles/loginpage.css'; // Import your dashboard.css file


function TheLogin() {
  const logo = '/images/ARRM_logo.png';
  const logo_2 = '/images/background_image.png';


  return (
    <div className="bg-container relative flex">
      {/* Login Page with background image behind */}
      <div className="left-half bg-gradient-to-r from-red-500 to-white h-screen flex relative">
        {/* Background image behind the login page */}
        <img src={logo_2} alt="Big design" className="absolute inset-0 w-full h-full object-cover z-0" />

        {/* The login page component */}
        <LoginPage />
      </div>

      {/* Background elements */}
      <div className="right-half bg-white">
        <img src={logo} alt="ARRM Logo" className="top-right-image" />
      </div>
    </div>
  );
}

export default TheLogin;
