import React from 'react';
import BackgroundPage from '../pages/backgroundpage.js';
import LoginPage from '../components/login.js';
import '../styles/loginpage.css'; // Import your dashboard.css file


function TheLogin() {
  const logo = '/images/ARRM_logo.png';
  const logo_2 = '/images/background_image.png';

  
  return (
    <div className="bg-container relative"> {/* Ensure proper positioning */}
      {/* Your existing background */}
      <div className="left-half bg-gradient-to-r from-red-500 to-white h-screen flex">
        {/* Render the login component on top */}
        <div className="absolute inset-0 flex justify-center items-center">
          <LoginPage />
        </div>
      </div>

      {/* Other parts of the background */}
      <div className="right-half bg-white">
        <img src={logo} alt="ARRM Logo" className="top-right-image" />
      </div>
      <div className="image-background">
        <img src={logo_2} alt="Big design" className="center-image" />
      </div>
    </div>
  );
  
}

export default TheLogin;
