import React from 'react';
import '../styles/background.css'; // Import your background.css file

function Background() {
  return (
    <div className="container">
      <div className="left-half bg-gradient-to-r from-red-500 to-white h-screen flex">
        <div className="w-1/2 bg-red-500 text-white flex flex-col justify-center items-center">
          <h1 className="text-6xl font-bold">Welcome to ARRM</h1>
          <p className="text-2xl mt-4">The perfect toolkit for managing your research projects.</p>
        </div>
      </div>
      <div className="right-half bg-white flex flex-col justify-center items-center">
        <button className="bg-red-500 text-white px-8 py-4 rounded-lg text-2xl font-bold">EXPLORE</button>
      </div>
    </div>
  );
}

export default Background;
