import React, { useState } from "react";
import '../styles/login.css';  // Import your login.css file

function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const logo = '/images/ARRM_logo.png'

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // perform your authentication logic here
    console.log(email, password);
  };

  return (
    <div className="container">
      <div className="flex items-center justify-center h-screen">
        <div className="bg-white p-8 rounded-lg shadow-md">
          <div className="flex flex-col items-center relative">
            <div className="image-container">
              <img src={logo} alt="ARRM Logo" className="centered-image" />
            </div>
        </div>
        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
    <div className="rounded-md shadow-sm -space-y-px">
      <div>
        <label htmlFor="email" className="block text-sm font-medium text-gray-700">
          Email
        </label>
        <input
          id="email"
          name="email"
          type="email"
          value={email}
          onChange={handleEmailChange}
          autoComplete="email"
          required
          className="input-field" // Add a class for styling purposes if needed
          placeholder="Email"
        />
      </div>
      <div>
        <label htmlFor="password" className="block text-sm font-medium text-gray-700">
          Password
        </label>
        <input
          id="password"
          name="password"
          type="password"
          value={password}
          onChange={handlePasswordChange}
          autoComplete="current-password"
          required
          className="input-field" // Add a class for styling purposes if needed
          placeholder="Password"
        />
      </div>
    </div>

          <div className="flex items-center justify-between">
          
            <div className="text-sm ml-4"> {/* Add margin-left to create space */}
              {/* eslint-disable-next-line jsx-a11y/anchor-is-valid */}
              <a 
                href="#"
                className="font-medium text-indigo-600 hover:text-indigo-500"
              >
                Forgot your password?
              </a>
            </div>
          </div>

          <div>
            <button
              type="submit"
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              Sign in
            </button>
          </div>
        </form>
      </div>
    </div>
    </div>
  );
}

export default LoginPage;
