import React from 'react';
import BackgroundPage from '../pages/backgroundpage.js';
// import LoginPage from '../components/login.js';
import '../styles/background.css'; // Import your background.css file
// import '../styles/login.css'; // Import your login.css file
import '../styles/loginpage.css'; // Import your dashboard.css file


function TheLogin() {
  return (
    <div className="bg-container">
      <div className="left-half bg-gradient-to-r from-red-500 to-white h-screen flex">
        <LoginPage />
      </div>
      <div className="right-half bg-white">
        <img src="/images/ARRM_logo.png" alt="ARRM Logo" className="top-right-image" />
      </div>
      <div className="image-background">
        <img src="/images/background_image.png" alt="Big design" className="center-image" />
      </div>
    </div>
  );
  
}

export default TheLogin;
